clearNN = nnCreate(2,10,1);trainingInputs = 0.3*rand(2,10000000)+0.1;trainingOutputs = trainingInputs(1,:) + trainingInputs(2,:);[newNN, trainError] = nnTrain(NN, 10, 1, trainingInputs, trainingOutputs);validationInputs = 0.3*rand(2,10)+0.1;validationOutputs = nnSim(newNN,validationInputs);[validationInputs' validationOutputs' (validationInputs(1,:).+validationInputs(2,:))']plot(trainError);